definition.script:
import hudson.model.*
import hudson.model.AbstractProject
import hudson.tasks.Mailer

def build_ok = true


BUILD_JOB_NAME = "${JOB_NAME}"

pipeline {
    agent { node { label '{{label}}' } }
    stages {
        {% include './partials/scripts/_set_build_name.groovy' with context %}
        {% include './partials/scripts/_slack_notification_starting.groovy' with context %}
        stage('Clean') {
            steps {
                script {
                    try {
                        sh "cd ./PedidosYaAS && rm -rf build  && cd .."
                        sh "/usr/bin/git reset HEAD --hard"
                    } catch (exc) {}
                }
            }
        }
        stage('Preparation') {
            steps {
                checkout([$class: 'GitSCM',
                    branches: [[name: "{{branch}}"]],
                    extensions: [[$class: 'CloneOption', timeout: 120]],
                    gitTool: 'Default',
                    userRemoteConfigs: [[url: '{{repository}}']]
                ])
            }
        }
        stage('Build') {
            steps {
                script {
                    try {
                        sh "cd ./PedidosYaAS && /usr/bin/gradle clean assembleapp24Stagingapp24Release"
                    } catch (exc) {
                        currentBuild.result = 'FAILED'
                        build_ok = false
                    }
                }
            }
        }
        stage('Publish') {
            steps {
                script {
                    if(build_ok) {
                        sh 'cd ./PedidosYaAS/pedidosYa/build/outputs/apk/app24Staging/app24Release && zip -r pedidosYa-app24-staging-app24Release.apk.zip pedidosYa-app24-staging-app24Release.apk'
                        sh 'mv ./PedidosYaAS/pedidosYa/build/outputs/apk/app24Staging/app24Release/pedidosYa-app24-staging-app24Release.apk.zip ./Apetito24.apk.zip'
                        archiveArtifacts allowEmptyArchive: false, artifacts: 'Apetito24.apk.zip', onlyIfSuccessful: true
                    }
                }
            }
        }
        stage('Get App Version') {
            steps {
                script {
                    if(build_ok) {
                        def version = sh returnStdout: true, script: """
                        a=\$(grep -F "version_name=" PedidosYaAS/gradle.properties)
                        IFS='='
                        read -ra b <<< \"\$a\"
                        version=\"\${b[1]}\"
                        echo \$version
                        """
                        version = version.trim()
                        def bundle_version = sh returnStdout: true, script: """
                        a=\$(grep -F "app24_version_code=" PedidosYaAS/gradle.properties)
                        IFS='='
                        read -ra b <<< \"\$a\"
                        version=\"\${b[1]}\"
                        echo \$version
                        """
                        bundle_version = bundle_version.trim()
                        version = version + '_' + bundle_version
                        print('{"version": "' + version + '"}')
                        httpRequest consoleLogResponseBody: true, acceptType: 'APPLICATION_JSON_UTF8', contentType: 'APPLICATION_JSON_UTF8', httpMode: 'PUT', ignoreSslErrors: true, requestBody: '{"version": "' + version + '"}', responseHandle: 'NONE', url: 'https://us-central1-atenea-33daf.cloudfunctions.net/api/projects/{{athenea.project_id}}/environments/{{athenea.environment_id}}/version', validResponseCodes: '100:599'
                        {% if peya_app_id != '' %}
                        httpRequest consoleLogResponseBody: true, acceptType: 'APPLICATION_JSON_UTF8', contentType: 'APPLICATION_JSON_UTF8', httpMode: 'PUT', ignoreSslErrors: true, requestBody: '{"app_version": "' + version + '", "branch": "{{branch}}", "file_format": "apk", "build_url": "{{jenkins_url}}{{job_path}}/lastSuccessfulBuild/artifact/Apetito24.apk.zip"}', responseHandle: 'NONE', url: 'https://us-central1-peyaapps.cloudfunctions.net/Api/apps/{{peya_app_id}}', validResponseCodes: '100:599'
                        {% endif %}
                    }
                }
            }
        }
        {% if job_of_test != '' %}
        stage('Fire Tests') {
            steps {
                script {
                    if(build_ok) {
                        def jenkins_url = "{{jenkins_url}}"
                        build job:
                            "{{job_of_test}}",
                            parameters: [
                                string(name: 'Suite', value: "REGRESSION"),
                                string(name: 'App_Path', value: "{{jenkins_url}}{{job_path}}/${BUILD_NUMBER}/artifact"),
                                string(name: 'App_Name', value: "Apetito24.apk.zip"),
                                string(name: 'Reason_Of_Execution', value: "Nueva versión de la aplicación")
                            ],
                            propagate: false,
                            wait: false
                    }
                }
            }
        }
        {% endif %}
        {% include './partials/scripts/_slack_notification_finishing.groovy' with context %}
    }
}
