definition.script:
import hudson.model.*
import hudson.model.AbstractProject
import hudson.tasks.Mailer


BUILD_JOB_NAME = "${JOB_NAME}"

pipeline {
    agent { node { label 'APP_RELEASE_Android' } }
    {% include './partials/scripts/android/_environment_vars.groovy' with context %}
    stages {
        {% include './partials/scripts/_set_build_name.groovy' with context %}
        {% include './partials/scripts/_slack_notification_starting.groovy' with context %}
        stage('Clean') {
            steps {
                script {
                    try {
                        sh "cd ./PedidosYaAS && rm -rf build  && cd .."
                        sh "/usr/bin/git reset HEAD --hard"
                    } catch (exc) {}
                }
            }
        }
        stage('Preparation') {
            steps {
                checkout([$class: 'GitSCM',
                    branches: [[name: "${params.Branch}"]],
                    extensions: [[$class: 'CloneOption', timeout: 120]],
                    gitTool: 'Default',
                    userRemoteConfigs: [[url: '{{repository}}']]
                ])
            }
        }
        stage('Build') {
            steps {
                sh "cd ./PedidosYaAS && export ANDROID_HOME=/Users/jenkins/Library/Android/sdk &&export LANG=en_US.UTF-8 && export FASTLANE_XCODE_LIST_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_RETRIES=13 && /usr/local/bin/fastlane build squad:FASTLANE_TEST type:demo"
            }
        }
        {% include './partials/scripts/_slack_notification_finishing.groovy' with context %}
    }
}
