import hudson.model.*
import hudson.model.AbstractProject
import hudson.tasks.Mailer


BUILD_JOB_NAME = "${JOB_NAME}"

pipeline {
    agent { node { label 'BUILD_iOS' } }
    stages {
        stage('Slack Notification Starting') {
            steps {
                script {
                    slackSend channel: '{{slack_channel}}', message: "Build ${env.JOB_NAME.replace('/', ' > ')} - #${env.BUILD_DISPLAY_NAME} started (<${env.BUILD_URL}|Open>)", color: '#439FE0', botUser: true, teamDomain: 'deliveryhero', tokenCredentialId: 'SlackToken'
                }
            }
        }
        stage('Clean') {
            steps {
                script {
                    try {
                        sh "cd ./PedidosYaAS && rm -rf build  && cd .."
                        sh "/usr/bin/git reset HEAD --hard"
                    } catch (exc) {}
                }
            }
        }
        stage('Preparation') {
            steps {
                script {
                    currentBuild.displayName = "Build_" + "${BUILD_TIMESTAMP}"
                }
                checkout([$class: 'GitSCM',
                    branches: [[name: "{{branch}}"]],
                    extensions: [[$class: 'CloneOption', timeout: 120]],
                    gitTool: 'Default',
                    userRemoteConfigs: [[url: '{{repository}}']]
                ])
            }
        }
        stage('Build') {
            steps {
                sh "export LANG=en_US.UTF-8 && export FASTLANE_XCODE_LIST_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_RETRIES=13 && /usr/local/bin/fastlane build squad:FASTLANE_TEST type:demo"
            }
        }
    }
}

