import hudson.model.*
import hudson.model.AbstractProject
import hudson.tasks.Mailer


BUILD_JOB_NAME = "${JOB_NAME}"

pipeline {
    agent { node { label '{{label}}' } }
    stages {
        stage('Preparation') {
            steps {
                script {
                    currentBuild.displayName = "Build_" + "${BUILD_TIMESTAMP}"
                }
                checkout([$class: 'GitSCM',
                    branches: [[name: "${params.Branch}"]],
                    extensions: [[$class: 'CloneOption', timeout: 120]],
                    gitTool: 'Default',
                    userRemoteConfigs: [[url: '{{repository}}']]
                ])
            }
        }
        stage('Slack Notification') {
            steps {
                script {
                    slackSend channel: '{{slack_channel}}', message: "Build ${env.JOB_NAME.replace('/', ' > ')} - #${env.BUILD_DISPLAY_NAME} started (<${env.BUILD_URL}|Open>)", color: '#439FE0', botUser: true, teamDomain: 'deliveryhero', tokenCredentialId: 'SlackToken'
                }
            }
        }
        stage('Build') {
            steps {
                script {
                    try {
                        sh "cd ./PedidosYaAS && /usr/bin/gradle clean assemblePeyaStagingPeyaRelease"
                    } catch (exc) {
                        slackSend channel: '{{slack_channel}}', message: "Application build ${env.JOB_NAME.replace('/', ' > ')} - #${env.BUILD_DISPLAY_NAME} was ${status} (<${env.BUILD_URL}|Open>)", color: "${color}", botUser: true, teamDomain: 'deliveryhero', tokenCredentialId: 'SlackToken'
                    }
                }
            }
        }
        stage('Publish') {
            steps {
                sh 'cd ./PedidosYaAS/pedidosYa/build/outputs/apk/peyaStaging/peyaRelease && zip -r pedidosYa-peya-staging-peyaRelease.apk.zip pedidosYa-peya-staging-peyaRelease.apk'
                sh 'mv ./PedidosYaAS/pedidosYa/build/outputs/apk/peyaStaging/peyaRelease/pedidosYa-peya-staging-peyaRelease.apk.zip ./pedidosYa-peya-staging-peyaRelease.apk.zip'
                archiveArtifacts allowEmptyArchive: false, artifacts: 'pedidosYa-peya-staging-peyaRelease.apk.zip', onlyIfSuccessful: true
            }
        }

        stage('Get App Version') {
            steps {
                script {
                    def version = sh returnStdout: true, script: """
                    a=\$(grep -F "peya_version_name=" PedidosYaAS/gradle.properties)
                    IFS='='
                    read -ra b <<< \"\$a\"
                    version=\"\${b[1]}\"
                    echo \$version
                    """
                    version = version.trim()
                    def bundle_version = sh returnStdout: true, script: """
                    a=\$(grep -F "peya_version_code=" PedidosYaAS/gradle.properties)
                    IFS='='
                    read -ra b <<< \"\$a\"
                    version=\"\${b[1]}\"
                    echo \$version
                    """
                    bundle_version = bundle_version.trim()
                    version = version + '_' + bundle_version
                    print('{"version": "' + version + '"}')
                    httpRequest consoleLogResponseBody: true, acceptType: 'APPLICATION_JSON_UTF8', contentType: 'APPLICATION_JSON_UTF8', httpMode: 'PUT', ignoreSslErrors: true, requestBody: '{"version": "' + version + '"}', responseHandle: 'NONE', url: 'https://us-central1-atenea-33daf.cloudfunctions.net/api/projects/{{athenea.project_id}}/environments/{{athenea.environment_id}}/version', validResponseCodes: '100:599'
                }
            }
        }
        {% if job_of_test != '' %}
        stage('Fire Tests') {
            steps {
                script {
                Jenkins.instance.getAllItems(AbstractItem.class).each {
                    if (BUILD_JOB_NAME == it.fullName) {
                        def user_id = it.getLastBuild().getCauses()[0].getUserId()

                        def u = User.get(user_id)
                        def user_name = it.getLastBuild().getCauses()[0].getUserName()
                        def user_email = u.getProperty(Mailer.UserProperty.class).getAddress()
                        def emails_to_notify = user_email + ",${params.Emails_To_Notify}"
                        def jenkins_url = "{{jenkins_url}}"
                        build job:
                            "{{job_of_test}}",
                            parameters: [
                                string(name: 'Suite', value: "${params.Test_To_Execute}"),
                                string(name: 'App_Path', value: "{{jenkins_url}}{{job_path}}/${BUILD_NUMBER}/artifact"),
                                booleanParam(name: 'Testrail_Flag', value: false),
                                string(name: 'Emails_To_Notify', value: emails_to_notify),
                                string(name: 'Reason_Of_Execution', value: "Ejecución de pruebas automatizadas sobre el branch «${params.Branch}»"),
                                string(name: 'Started_By', value: user_name)
                            ],
                            propagate: false,
                            wait: false

                    }
                };
            }
        }
        {% endif %}
    }
}

