definition.script:
import hudson.model.*
import hudson.model.AbstractProject
import hudson.tasks.Mailer

def build_ok = true
def test_ok = true
def lint_ok = true


BUILD_JOB_NAME = "${JOB_NAME}"

pipeline {
    agent { node { label '{{label}}' } }
    stages {
        {% include './partials/scripts/_set_build_name.groovy' with context %}
        {% include './partials/scripts/_slack_notification_starting.groovy' with context %}
        stage('Clean') {
            steps {
                script {
                    try {
                        sh "cd ./PedidosYaAS && rm -rf build"
                    } catch (exc) {}
                }
            }
        }
        stage('Preparation') {
            steps {
                checkout([$class: 'GitSCM',
                    branches: [[name: "${params.Branch}"]],
                    extensions: [[$class: 'CloneOption', timeout: 120]],
                    gitTool: 'Default',
                    userRemoteConfigs: [[url: '{{repository}}']]
                ])
            }
        }
        stage('Clean') {
            steps {
                script {
                    sh "cd ./PedidosYaAS && /usr/bin/gradle clean"
                }
            }
        }
        stage('Unit Tests') {
            steps {
                script {
                    try {
                        sh "cd ./PedidosYaAS && /usr/bin/gradle testPeyaStagingPeyaReleaseUnitTest"
                    } catch (exc) {
                        currentBuild.result = 'FAILED'
                        test_ok = false
                    }
                }
            }
        }
        stage('Lint') {
            steps {
                script {
                    try {
                        sh "cd ./PedidosYaAS && /usr/bin/gradle lintPeyaStagingPeyaRelease"
                    } catch (exc) {
                        currentBuild.result = 'FAILED'
                        lint_ok = false
                    }
                }
            }
        }
        stage('Build') {
            steps {
                script {
                    try {
                        sh "cd ./PedidosYaAS && /usr/bin/gradle assemblePeyaStagingPeyaRelease"
                    } catch (exc) {
                        currentBuild.result = 'FAILED'
                        build_ok = false
                    }
                    sh "cd .."
                }
            }
        }
        stage('Set Status') {
            steps {
                script {
                    build job:
                        "PullRequestChecker/Set_PR_Status",
                        parameters: [
                            string(name: 'SHA', value: "${params.Branch}"),
                            string(name: 'Repository', value: "android-mobile"),
                            string(name: 'JobUrl', value: "${env.BUILD_URL}"),
                            booleanParam(name: 'Status', value: build_ok)
                        ],
                        propagate: false,
                        wait: false
                }
            }
        }
        {% include './partials/scripts/_slack_notification_finishing.groovy' with context %}
    }
}
