import hudson.model.*
import hudson.model.AbstractProject
import hudson.tasks.Mailer

{% include 'partials/scripts/_retry_function.groovy' ignore missing with context %}


BUILD_JOB_NAME = "${JOB_NAME}"

pipeline {
    agent { node { label '{{label}}' } }
    stages {
        {% include 'partials/scripts/_set_build_name.groovy.groovy' ignore missing with context %}
        {% include 'partials/scripts/_slack_notification_starting.groovy' ignore missing with context %}
        stage('Preparation') {
            steps {
                checkout([$class: 'GitSCM',
                    branches: [[name: "${params.Branch}"]],
                    extensions: [[$class: 'CloneOption', timeout: 120]],
                    gitTool: 'Default',
                    userRemoteConfigs: [[url: '{{repository}}']]
                ])
            }
        }
        {% include 'partials/scripts/ios/_unlock_keychain.groovy' ignore missing with context %}
        {% include 'partials/scripts/ios/_install_pods.groovy' ignore missing with context %}
        stage ('Build Demo Release Candidate') {
            when {
                expression { params.Lane == 'DemoRC' }
            }
            steps {
                script {
                    try {
                        sh "export LANG=en_US.UTF-8 && export FASTLANE_XCODE_LIST_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_RETRIES=13 && /usr/local/bin/fastlane demoRC"
                    } catch (exc) {
                        currentBuild.result = 'FAILED'
                        build_ok = false
                    }
                }
            }
        }
        stage ('Build Night') {
            when {
                expression { params.Lane == 'Night' }
            }
            steps {
                script {
                    try {
                        sh "export LANG=en_US.UTF-8 && export FASTLANE_XCODE_LIST_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_RETRIES=13 && /usr/local/bin/fastlane night"
                    } catch (exc) {
                        currentBuild.result = 'FAILED'
                        build_ok = false
                    }
                }
            }
        }
        stage ('Build Release') {
            when {
                expression { params.Lane == 'Release' }
            }
            steps {
                script {
                    try {
                        sh "export LANG=en_US.UTF-8 && export FASTLANE_XCODE_LIST_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_RETRIES=13 && /usr/local/bin/fastlane release"
                    } catch (exc) {
                        currentBuild.result = 'FAILED'
                        build_ok = false
                    }
                }
            }
        }
        stage ('Build Demo Night') {
            when {
                expression { params.Lane == 'Demo Night' }
            }
            steps {
                script {
                    try {
                        sh "export LANG=en_US.UTF-8 && export FASTLANE_XCODE_LIST_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_RETRIES=13 && /usr/local/bin/fastlane demoNight"
                    } catch (exc) {
                        currentBuild.result = 'FAILED'
                        build_ok = false
                    }
                }
            }
        }
        {% include 'partials/scripts/_slack_notification_finishing.groovy' ignore missing with context %}
    }
}

