import hudson.model.*
import hudson.model.AbstractProject
import hudson.tasks.Mailer


BUILD_JOB_NAME = "${JOB_NAME}"

pipeline {
    agent { node { label '{{label}}' } }
    stages {
        stage('Set Build Name') {
            steps {
                script {
                    currentBuild.displayName = "Build_" + "${BUILD_TIMESTAMP}"
                }
            }
        }
        stage('Slack Notification Starting') {
            steps {
                script {
                    slackSend channel: '{{slack_channel}}', message: "Build ${env.JOB_NAME.replace('/', ' > ')} - #${env.BUILD_DISPLAY_NAME} started (<${env.BUILD_URL}|Open>)", color: '#439FE0', botUser: true, teamDomain: 'deliveryhero', tokenCredentialId: 'SlackToken'
                }
            }
        }
        stage('Preparation') {
            steps {
                checkout([$class: 'GitSCM',
                    branches: [[name: "${params.Branch}"]],
                    extensions: [[$class: 'CloneOption', timeout: 120]],
                    gitTool: 'Default',
                    userRemoteConfigs: [[url: '{{repository}}']]
                ])
            }
        }
        stage('Unlock Keychain') {
            steps {
                sh "security unlock-keychain -p Automation01 ~/Library/Keychains/login.keychain"
            }
        }
        stage('Install Pods') {
            steps {
                script {
                    try {
                        sh "rm Podfile.lock"
                        sh "export LANG=en_US.UTF-8 && /usr/local/bin/pod repo update"
                        sh "export LANG=en_US.UTF-8 && /usr/local/bin/pod install"
                    } catch (exc) {
                        currentBuild.result = 'FAILED'
                        build_ok = false
                    }
                }
            }
        }
        stage ('Build Demo Release Candidate') {
            when {
                expression { params.Lane == 'DemoRC' }
            }
            steps {
                script {
                    try {
                        sh "export LANG=en_US.UTF-8 && export FASTLANE_XCODE_LIST_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_RETRIES=13 && /usr/local/bin/fastlane demoRC"
                    } catch (exc) {
                        currentBuild.result = 'FAILED'
                        build_ok = false
                    }
                }
            }
        }
        stage ('Build Night') {
            when {
                expression { params.Lane == 'Night' }
            }
            steps {
                script {
                    try {
                        sh "export LANG=en_US.UTF-8 && export FASTLANE_XCODE_LIST_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_RETRIES=13 && /usr/local/bin/fastlane night"
                    } catch (exc) {
                        currentBuild.result = 'FAILED'
                        build_ok = false
                    }
                }
            }
        }
        stage ('Build Release') {
            when {
                expression { params.Lane == 'Release' }
            }
            steps {
                script {
                    try {
                        sh "export LANG=en_US.UTF-8 && export FASTLANE_XCODE_LIST_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_RETRIES=13 && /usr/local/bin/fastlane release"
                    } catch (exc) {
                        currentBuild.result = 'FAILED'
                        build_ok = false
                    }
                }
            }
        }
        stage ('Build Demo Night') {
            when {
                expression { params.Lane == 'Demo Night' }
            }
            steps {
                script {
                    try {
                        sh "export LANG=en_US.UTF-8 && export FASTLANE_XCODE_LIST_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_TIMEOUT=120 && export FASTLANE_XCODEBUILD_SETTINGS_RETRIES=13 && /usr/local/bin/fastlane demoNight"
                    } catch (exc) {
                        currentBuild.result = 'FAILED'
                        build_ok = false
                    }
                }
            }
        }
        stage('Slack Notification Finishing') {
            steps {
                script {
                    def status = ""
                    def color = ""
                    if(build_ok) {
                        status = "success"
                        color = "good"
                    } else {
                        status = "failed"
                        color = "danger"
                    }
                    slackSend channel: '{{slack_channel}}', message: "Build ${env.JOB_NAME.replace('/', ' > ')} - #${env.BUILD_DISPLAY_NAME} was ${status} after ${currentBuild.durationString.replace(' and counting', '')} (<${env.BUILD_URL}|Open>)", color: "${color}", botUser: true, teamDomain: 'deliveryhero', tokenCredentialId: 'SlackToken'
                }
            }
        }
    }
}

