import hudson.model.*
import hudson.model.AbstractProject
import hudson.tasks.Mailer

{% include './partials/scripts/_retry_function.groovy' with context %}

def build_ok = true


BUILD_JOB_NAME = "${JOB_NAME}"

pipeline {
    agent { node { label '{{label}}' } }
    stages {
        {% include './partials/scripts/_set_build_name.groovy' with context %}
        {% include './partials/scripts/_slack_notification_starting.groovy' with context %}
        stage('Preparation') {
            steps {
                checkout([$class: 'GitSCM',
                    branches: [[name: "${params.Branch}"]],
                    extensions: [[$class: 'CloneOption', timeout: 120]],
                    gitTool: 'Default',
                    userRemoteConfigs: [[url: '{{repository}}']]
                ])
            }
        }
        {% include './partials/scripts/ios/_unlock_keychain.groovy' with context %}
        {% include './partials/scripts/ios/_install_pods.groovy' with context %}
        stage ('Build Demo') {
            when {
                expression { params.Lane == 'Demo' }
            }
            steps {
                script {
                    try {
                        sh "export LANG=en_US.UTF-8 && export FASTLANE_XCODE_LIST_TIMEOUT=1200 && export FASTLANE_XCODEBUILD_SETTINGS_TIMEOUT=1200 && export FASTLANE_XCODEBUILD_SETTINGS_RETRIES=13 && /usr/local/bin/fastlane demo release_notes:\"${params.Release_Note}\" squad:\"${params.Squad}\""
                    } catch (exc) {
                            currentBuild.result = 'FAILED'
                            build_ok = false
                    }
                }
            }
        }
        stage ('Build Peya Demo') {
            when {
                expression { params.Lane == 'Peya Demo' }
            }
            steps {
                script {
                    try {
                        sh "export LANG=en_US.UTF-8 && export FASTLANE_XCODE_LIST_TIMEOUT=1200 && export FASTLANE_XCODEBUILD_SETTINGS_TIMEOUT=1200 && export FASTLANE_XCODEBUILD_SETTINGS_RETRIES=13 && /usr/local/bin/fastlane peyaDemo release_notes:\"${params.Release_Note}\" squad:\"${params.Squad}\""
                    } catch (exc) {
                            currentBuild.result = 'FAILED'
                            build_ok = false
                    }
                }
            }
        }
        {% include './partials/scripts/_slack_notification_finishing.groovy' with context %}
    }
}

        true


    false
