import hudson.model.*
import hudson.model.AbstractProject
import hudson.tasks.Mailer

def build_ok = true


BUILD_JOB_NAME = "${JOB_NAME}"

pipeline {
    agent { node { label '{{label}}' } }
    stages {
        stage('Slack Notification Starting') {
            steps {
                script {
                    slackSend channel: '{{slack_channel}}', message: "Build ${env.JOB_NAME.replace('/', ' > ')} - #${env.BUILD_DISPLAY_NAME} started (<${env.BUILD_URL}|Open>)", color: '#439FE0', botUser: true, teamDomain: 'deliveryhero', tokenCredentialId: 'SlackToken'
                }
            }
        }
        stage('Preparation') {
            steps {
                script {
                    currentBuild.displayName = "Build_" + "${BUILD_TIMESTAMP}"
                }
                checkout([$class: 'GitSCM',
                    branches: [[name: "${params.Branch}"]],
                    extensions: [[$class: 'CloneOption', timeout: 120]],
                    gitTool: 'Default',
                    userRemoteConfigs: [[url: '{{repository}}']]
                ])
            }
        }
        stage('Unlock Keychain') {
            steps {
                sh "security unlock-keychain -p Automation01 ~/Library/Keychains/login.keychain"
            }
        }
        stage('Install Pods') {
            steps {
                script {
                    try {
                        sh "rm Podfile.lock"
                        sh "export LANG=en_US.UTF-8 && /usr/local/bin/pod repo update"
                        sh "export LANG=en_US.UTF-8 && /usr/local/bin/pod install"
                    } catch (exc) {
                            currentBuild.result = 'FAILED'
                            build_ok = false
                    }
                }
            }
        }
        stage ('Build Demo') {
            when {
                expression { params.Lane == 'Demo' }
            }
            steps {
                script {
                    try {
                        sh "export LANG=en_US.UTF-8 && export FASTLANE_XCODE_LIST_TIMEOUT=1200 && export FASTLANE_XCODEBUILD_SETTINGS_TIMEOUT=1200 && export FASTLANE_XCODEBUILD_SETTINGS_RETRIES=13 && /usr/local/bin/fastlane demo release_notes:\"${params.Release_Note}\" squad:\"${params.Squad}\""
                    } catch (exc) {
                            currentBuild.result = 'FAILED'
                            build_ok = false
                    }
                }
            }
        }
        stage ('Build Peya Demo') {
            when {
                expression { params.Lane == 'Peya Demo' }
            }
            steps {
                script {
                    try {
                        sh "export LANG=en_US.UTF-8 && export FASTLANE_XCODE_LIST_TIMEOUT=1200 && export FASTLANE_XCODEBUILD_SETTINGS_TIMEOUT=1200 && export FASTLANE_XCODEBUILD_SETTINGS_RETRIES=13 && /usr/local/bin/fastlane peyaDemo release_notes:\"${params.Release_Note}\" squad:\"${params.Squad}\""
                    } catch (exc) {
                            currentBuild.result = 'FAILED'
                            build_ok = false
                    }
                }
            }
        }
        stage('Slack Notification Finishing') {
            steps {
                script {
                    def status = ""
                    def color = ""
                    if(build_ok) {
                        status = "success"
                        color = "good"
                    } else {
                        status = "failed"
                        color = "danger"
                    }
                    slackSend channel: '{{slack_channel}}', message: "Build ${env.JOB_NAME.replace('/', ' > ')} - #${env.BUILD_DISPLAY_NAME} was ${status} after ${currentBuild.durationString.replace(' and counting', '')} (<${env.BUILD_URL}|Open>)", color: "${color}", botUser: true, teamDomain: 'deliveryhero', tokenCredentialId: 'SlackToken'
                }
            }
        }
    }
}

